C<pre><code>struct Node {
    char *value;
    struct Node *next;
};
</code></pre>

C<pre><code>struct Node* addNode(char *s, struct Node *list) {
    struct Node *newNode = (struct Node*)malloc(sizeof(struct Node));
    newNode-&gt;value = (char*)malloc(strlen(s) + 1); // +1 for '\0'
    strcpy(newNode-&gt;value, s);
    newNode-&gt;next = list;
    return newNode;
}
</code></pre>

C<pre><code>void freeList(struct Node *list) {
    while (list != NULL) {
        struct Node *temp = list;
        list = list-&gt;next;
        free(temp-&gt;value); // Free the string
        free(temp);        // Free the node
    }
}
</code></pre>

